import Block from "../block.js";
/**
 * @ignore
 */
export default class IfBlock extends Block {
    constructor(condition) {
        super();
        this.condition = condition;
    }
    static If(condition) {
        return new IfBlock(condition);
    }
    then(block) {
        return this;
    }
    else(block) {
        return this;
    }
}
